import React, { useState, useEffect, useCallback } from 'react';
import { Link, NavLink } from 'react-router-dom';
import { useUser } from '../functions/userContext';
import Car from './Car';
import '../styles/Preferences.css';
import logo from '../images/EVS FOR ALL.png';
import car1 from '../images/carpic1.jpeg';

const Preferences = () => {
  const { user: currentUser } = useUser();
  const [starredVehicles, setStarredVehicles] = useState([]);

  const fetchStarredVehicles = useCallback(async () => {
    if (currentUser) {
      try {
        const response = await fetch(`/user-stars?username=${currentUser.username}`, {
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('token')}`
          }
        });
        if (response.ok) {
          const data = await response.json();
          setStarredVehicles(data);
        } else {
          console.error("Failed to fetch starred vehicles");
        }
      } catch (error) {
        console.error("Error fetching starred vehicles:", error);
      }
    }
  }, [currentUser]);

  useEffect(() => {
    fetchStarredVehicles();
  }, [fetchStarredVehicles]);


  const cars = [
    { id: 1, name: 'Audi e-tron GT 2022', price: 30000, image: car1, mpg: 94, capacity: 5 },
    { id: 2, name: 'Audi e-tron GT 2021', price: 20000, image: car1, mpg: 94, capacity: 5 },
    { id: 3, name: 'Audi e-tron GT 2024', price: 10000, image: car1, mpg: 94, capacity: 5 },
    { id: 4, name: 'Audi e-tron GT 2012', price: 40000, image: car1, mpg: 94, capacity: 5 },
    { id: 5, name: 'Audi e-tron GT 2011', price: 50000, image: car1, mpg: 94, capacity: 5 },
    { id: 6, name: 'Audi e-tron GT 2010', price: 70000, image: car1, mpg: 94, capacity: 5 },
    { id: 1, name: 'Audi e-tron GT 2022', price: 30000, image: car1, mpg: 94, capacity: 5 },
    { id: 1, name: 'Audi e-tron GT 2022', price: 30000, image: car1, mpg: 94, capacity: 5 },
    { id: 1, name: 'Audi e-tron GT 2022', price: 30000, image: car1, mpg: 94, capacity: 5 }
  ];
  
  return (
      <div className="preferences-container">
        <div className="preferences-navbar">
          <Link to="/HomePage" title="EVs for All Website">
            <img src={logo} alt="EVs for All Website" />
          </Link>
          <NavLink to="/HomePage" className="preferences-navlink" activeClassName="active-navlink">
            Return Home
          </NavLink>
          <NavLink to="/Preferences" className="preferences-navlink" activeClassName="active-navlink">
            Profile Settings
          </NavLink>
          <NavLink to="/Preferences" className="preferences-navlink" activeClassName="active-navlink">
            Location Settings
          </NavLink>
          <NavLink to="/Preferences" className="preferences-navlink" activeClassName="active-navlink">
            Preferences
          </NavLink>
          <NavLink to="/Preferences" className="preferences-navlink" activeClassName="active-navlink">
            Help
          </NavLink>
        </div>
        <div className="preferences-content">
        <h1>My Starred Vehicles</h1>
        {starredVehicles.length > 0 ? (
          <div className="starred-vehicles">
            {starredVehicles.map(vehicle => (
              <Car key={vehicle.id} {...vehicle} />
            ))}
          </div>
        ) : (
          <p>No vehicles starred yet.</p>
        )}
      </div>
    </div>
  );
};

export default Preferences;
