      <div className="buy-page">
        {cars.map((car) => (
          <Car
            key={car.id}
            id={car.id}
            name={car.name}
            price={car.price}
            image={car.image}
            mpg={car.mpg}
            capacity={car.capacity}
            isStarred={starredStatus[car.id] }
            onStar={() => handleStar(currentUser,car.id, car.price)}
            onUnstar={() => handleUnstar(currentUser,car.id)}
          />
        ))}
      </div>
    </div>
  );
};

export default Buy;
