import React, { useState, useEffect,useCallback } from 'react';
import Car from './Car'; // Import the Car component
import { useUser } from '../functions/userContext'; // Import the useUser hook
import '../styles/Buy.css';
import car1 from '../images/carpic1.jpeg'; // Update the correct path if needed
import {Link, NavLink} from 'react-router-dom';
import profpic from '../images/profpic.png' ;
import logo from '../images/EVS FOR ALL.png';

const Buy = () => {
  const { user: currentUser } = useUser(); // Use the useUser hook to access current user
  const [starredVehicles, setStarredVehicles] = useState([]);

  const fetchStarredVehicles = useCallback(async () => {
    // API call to get starred vehicles for the current user
    if (currentUser) {
      const response = await fetch(`/user-stars?username=${currentUser.username}`);
      if (response.ok) {
        const data = await response.json();
        setStarredVehicles(data);
      }
    }
  }, [currentUser]); // fetchStarredVehicles will only change when currentUser changes

  useEffect(() => {
    fetchStarredVehicles();
  }, [fetchStarredVehicles]);

  const handleStar = async (vehicleId) => {
    // API call to star a vehicle
    await fetch('/star-vehicle', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({ username: currentUser.username, vehicle_id: vehicleId })
    });
    fetchStarredVehicles();
  };

  const handleUnstar = async (vehicleId) => {
    // API call to unstar a vehicle
    await fetch('/unstar-vehicle', {
      method: 'DELETE',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({ username: currentUser.username, vehicle_id: vehicleId })
    });
    fetchStarredVehicles();
};

  // Dummy data array for cars
  const cars = [
    { id: 1, name: 'Audi e-tron GT 2022', price: 30000, image: car1, mpg: 94, capacity: 5 },
    { id: 2, name: 'Audi e-tron GT 2021', price: 20000, image: car1, mpg: 94, capacity: 5 },
    { id: 3, name: 'Audi e-tron GT 2024', price: 10000, image: car1, mpg: 94, capacity: 5 },
    { id: 4, name: 'Audi e-tron GT 2012', price: 40000, image: car1, mpg: 94, capacity: 5 },
    { id: 5, name: 'Audi e-tron GT 2011', price: 50000, image: car1, mpg: 94, capacity: 5 },
    { id: 6, name: 'Audi e-tron GT 2010', price: 70000, image: car1, mpg: 94, capacity: 5 },
    { id: 1, name: 'Audi e-tron GT 2022', price: 30000, image: car1, mpg: 94, capacity: 5 },
    { id: 1, name: 'Audi e-tron GT 2022', price: 30000, image: car1, mpg: 94, capacity: 5 },
    { id: 1, name: 'Audi e-tron GT 2022', price: 30000, image: car1, mpg: 94, capacity: 5 }
  ];

  const [isDropdownVisible, setDropdownVisibility] = useState(false);

  // Function to toggle the visibility of the dropdown menu
  const toggleDropdown = () => {
    setDropdownVisibility(!isDropdownVisible);
  };
  return (
    <div>
                  <div id = "menubar">
            <Link to = "/" title = "EVs for All Website">
                <img src={logo} alt="EVs for All Website" />
            </Link>
            <nav id = "homenav">
                <a className = "navlinks" href='/HomePage'>Home</a>
                <a className = "navlinks" href='/Buy'>Buy EVs</a>
                <a className = "navlinks" href='/Buy'>Rent EVs</a>
                <a className = "navlinks" href='/HomePage'>Lease EVs</a>
                <a className = "navlinks" href='/HomePage'>Find EV Charging Stations</a>
                <a className = "navlinks" href='/HomePage'>More Information</a>
            </nav>
            <div id="profileImage" onClick={toggleDropdown}>
                <img src={profpic} alt="Profile" />

                {/* Dropdown menu */}
                {isDropdownVisible && (
                    <div className="dropdown-menu">
                    <ul>
                        <li><NavLink to="/profile-settings">Profile Settings</NavLink></li>
                        <li><NavLink to="/location-settings">Location Settings</NavLink></li>
                        <li><NavLink to="/preferences">Preferences</NavLink></li>
                        <li><NavLink to="/help">Help</NavLink></li>
                    </ul>
                    </div>
                )}
            </div>
            </div>
      <div className="buy-page">
        {cars.map((car) => (
          <Car
            key={car.id}
            id={car.id}
            name={car.name}
            price={car.price}
            image={car.image}
            mpg={car.mpg}
            capacity={car.capacity}
            isStarred={starredVehicles.includes(car.id)}
            onStar={() => handleStar(car.id)}
            onUnstar={() => handleUnstar(car.id)}
          />
        ))}
      </div>
    </div>
  );
};

export default Buy;
